import Chart from "./components/Chart";
import Selector from "./components/Selector";
import VolumeSlider from "./components/Slider";
import "./App.css";
import React, { Component } from "react";

class App extends Component {
  state = {
    selectedChart: "DayChart",
    soundlevels: {
      DayChart: [
        { name: "8AM", "Decibel Level": 2400 },
        { name: "10AM", "Decibel Level": 2100 },
        { name: "12AM", "Decibel Level": 2600 },
        { name: "2PM", "Decibel Level": 4000 },
        { name: "4PM", "Decibel Level": 2400 },
        { name: "6PM", "Decibel Level": 2100 },
        { name: "8PM", "Decibel Level": 2600 }
      ],
      WeekChart: [
        { name: "Page A", "Decibel Level": 2400 },
        { name: "Page B", "Decibel Level": 2100 },
        { name: "Page C", "Decibel Level": 2600 },
        { name: "Page D", "Decibel Level": 2700 }
      ],
      MonthChart: [
        { name: "Page A", "Decibel Level": 2400 },
        { name: "Page B", "Decibel Level": 2100 },
        { name: "Page C", "Decibel Level": 3000 },
        { name: "Page D", "Decibel Level": 2700 }
      ],
      YearChart: [
        { name: "Page B", "Decibel Level": 2100 },
        { name: "Page C", "Decibel Level": 2600 },
        { name: "Page D", "Decibel Level": 2700 }
      ]
    }
  };
  handleButtonClick = myChart => {
    this.setState({ selectedChart: myChart });
    return <Chart data={this.state.soundlevels[this.state.selectedChart]} />;
  };
  render() {
    return (
      <header className="App-header">
        {/* <VolumeSlider /> */}
        <Selector onButtonClick={this.handleButtonClick} />
        <Chart data={this.state.soundlevels[this.state.selectedChart]} />
      </header>
    );
  }

  updateSoundLevels = () => {
    console.log("update");
    let newDayLevel = [
      { name: "8AM", "Decibel Level": "2400" },
      { name: "10AM", "Decibel Level": 10 },
      { name: "12AM", "Decibel Level": 2600 },
      { name: "2PM", "Decibel Level": 2700 },
      { name: "4PM", "Decibel Level": 2400 },
      { name: "6PM", "Decibel Level": 2100 },
      { name: "8PM", "Decibel Level": 2600 }
    ];
    let newWeekLevel = [
      { name: "Page A", "Decibel Level": 2400 },
      { name: "Page B", "Decibel Level": 10 },
      { name: "Page C", "Decibel Level": 20 },
      { name: "Page D", "Decibel Level": 2700 }
    ];
    let newMonthLevel = [
      { name: "Page A", "Decibel Level": 2000 },
      { name: "Page B", "Decibel Level": 100 },
      { name: "Page C", "Decibel Level": 1000 },
      { name: "Page D", "Decibel Level": 2700 }
    ];
    let newYearLevel = [
      { name: "Page A", "Decibel Level": 2400 },
      { name: "Page B", "Decibel Level": 10 },
      { name: "Page C", "Decibel Level": 2600 },
      { name: "Page D", "Decibel Level": 500 }
    ];

    this.setState(prevState => ({
      soundlevels: {
        ...prevState.soundlevels,
        DayChart: newDayLevel,
        WeekChart: newWeekLevel,
        MonthChart: newMonthLevel,
        YearChart: newYearLevel
      }
    }));
    console.log("DayChart:", this.state.soundlevels.DayChart);
  };

  componentDidMount() {
    setInterval(this.updateSoundLevels, 5000);
  }
  componentWillUnmount() {
    clearInterval(this.interval);
  }
}

export default App;
